import React from 'react'
import { Grid,Paper, Avatar, TextField, Button, Typography } from '@material-ui/core'
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import axios from 'axios'

const Login = () =>{
    const paperStyle={padding :20,height:'73vh',width:300, margin:"20 auto"}
    const avatarStyle={backgroundColor:'#1bbd7e'}
    const btnstyle={margin:'8px 0'}

    const [email, setEmail] = React.useState();
    const [password, setPassword] = React.useState();

    axios({
        method: "POST",
        mode : 'cors',
        url: "http://localhost:8000/users/sign-in",
        data: {
          emailUser : email,
          passwordUser : password
        },
        withCredentials : true
      }).then(()=>{
        navigate("/confirmation");
      })

    return(
        <Grid >
            <Paper  style={paperStyle}>
                <Grid align='center'>
                     <Avatar style={avatarStyle}><LockOutlinedIcon/></Avatar>
                    <h2>Se connecter</h2>
                </Grid>
                <TextField label="Adresse Email" placeholder="Entre un email" fullWidth required/>
                <TextField label='Mot de passe' placeholder='Entrer un mot de passe' type='password' fullWidth required/>
                <Button type='submit' color='inherit' variant="contained" style={btnstyle} fullWidth>Se connecter</Button>
                <Typography >
                </Typography>
                <Typography > Avez-vous un compte ?
                    {/* <Link href="./signup"  >
                        S'inscrire
                    </Link> */}
                </Typography>
            </Paper>
        </Grid>
    )
}

export default Login
