import Header from './components/Header';
import Main from './components/Main';
import Basket from './components/Basket';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from "react-router-dom";
import axios from 'axios'
function App() {
  const [products, setProducts] = useState();
  const [cartItems, setCartItems] = useState([]);
  const onAdd = (product) => {
    const exist = cartItems.find((x) => x.idProduct === product.idProduct);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.idProduct === product.idProduct ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }]);
    }
  };
  const onRemove = (product) => {
    const exist = cartItems.find((x) => x.idProduct === product.idProduct);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.idProduct !== product.idProduct));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.idProduct === product.idProduct ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };

  const fetchProducts = async () => {
    await axios.get('http://localhost:8000/products/all', { withCredentials: true })
    .then((response)=> setProducts(response.data));
  };

  useEffect(() => {
    fetchProducts();
    }, []);

  return (
    <div className="App">


      <Header countCartItems={cartItems.length}></Header>
      <div className="row">
        <Main products={products} onAdd={onAdd}></Main>
        <Basket
          cartItems={cartItems}
          onAdd={onAdd}
          onRemove={onRemove}
        ></Basket>
      </div>
    </div>
  );
}

export default App;
