import React from 'react'
import { Grid,Paper, Avatar, TextField, Button, Typography,Link } from '@material-ui/core'
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const Login = () =>{
    const paperStyle={padding :20,height:'73vh',width:300, margin:"20 auto"}
    const avatarStyle={backgroundColor:'#1bbd7e'}
    const btnstyle={margin:'8px 0'}

    return(
        <Grid >
            <Paper  style={paperStyle}>
                <Grid align='center'>
                     <Avatar style={avatarStyle}><LockOutlinedIcon/></Avatar>
                    <h2>Se connecter</h2>
                </Grid>
                <TextField label="Nom d'utilisateur" placeholder="Entrer un nom d'utilisateur" fullWidth required/>
                <TextField label='Mot de passe' placeholder='Entrer un mot de passe' type='password' fullWidth required/>
                <FormControlLabel
                    control={
                    <Checkbox
                        name="checkedB"
                        color="inherit"
                    />
                    }
                    label="Se souvenir de moi"
                 />
                <Button type='submit' color='inherit' variant="contained" style={btnstyle} fullWidth>Se connecter</Button>
                <Typography >
                     <Link href="#" >
                        Mot de passe oubli√© ?
                </Link>
                </Typography>
                <Typography > Avez-vous un compte ?
                     {/* <Link href="./signup" onClick={()=>handleChange("event",1)} >
                        S'inscrire
                </Link> */}
                </Typography>
            </Paper>
        </Grid>
    )
}

export default Login
